"use strict";window.onload=function(){var r=document.querySelector(".countdown .hour"),c=document.querySelector(".countdown .min"),u=document.querySelector(".countdown .second");setInterval(function(){var o=(new Date(2020,5,4,18,0,0)-Date.now())/1e3,n=(Math.floor(o/86400),Math.floor(o%86400/3600)),e=Math.floor(o%3600/60),t=Math.floor(o%60);r.innerHTML=n,c.innerHTML=e,u.innerHTML=t},1e3)};